        uint256 valueQuantity;
        address payable bidderAddress;
        bytes32 blindBidPrice; // will have price and quantity you are bidding  
        bytes32 blindBidQuantity;
        uint limitingResourceQuantity;
    }
    uint totalBidders = 0;
    uint bidsTillNow = 0;

    mapping (address => Bid) bids;

